mapper.select:
  	select 

      from cost_record


			and t_id = #{tId,jdbcType=BIGINT}


			and a_id = #{aId,jdbcType=BIGINT}


			and lab_id = #{labId,jdbcType=BIGINT}


			and find_in_set(#{orgs,jdbcType=VARCHAR}, orgs)


			and state = #{state,jdbcType=INTEGER}


		 	and lab_result = #{labResult,jdbcType=INTEGER}


			and create_time >= #{startCreateTime, jdbcType=TIMESTAMP}


			and create_time <= #{endCreateTime, jdbcType=TIMESTAMP}


			and send_time >= #{startSendTime, jdbcType=TIMESTAMP}


			and send_time <= #{endSendTime, jdbcType=TIMESTAMP}





  			order by ${custom_order_sql}


  			order by id desc
