mapper.select:
  	select 

      from material


	        and type = #{type,jdbcType=INTEGER}



			and state = #{state,jdbcType=INTEGER}



	    	and create_time >= #{startCreateTime, jdbcType=TIMESTAMP}



	    	and create_time <= #{endCreateTime, jdbcType=TIMESTAMP}



	        and pro_no like concat('%', #{proNo,jdbcType=VARCHAR}, '%') 



	        and mat_name like concat('%', #{qmatName,jdbcType=VARCHAR}, '%') 



	        and mat_name = #{matName,jdbcType=VARCHAR}



	        and mat_no like concat('%', #{matNo,jdbcType=VARCHAR}, '%') 



	        and mat_producer like concat('%', #{matProducer,jdbcType=VARCHAR}, '%') 





  			order by ${custom_order_sql}


  			order by id desc
