mapper.select:
  	select 

      from parts


	        and code like concat('%', #{code,jdbcType=VARCHAR}, '%') 



	        and name like concat('%', #{name,jdbcType=VARCHAR}, '%') 



	        and type = #{type,jdbcType=INTEGER}



	    	and pro_time >= #{startProTime, jdbcType=DATE}



	    	and pro_time <= #{endProTime, jdbcType=DATE}



	    	and create_time >= #{startCreateTime, jdbcType=TIMESTAMP}



	    	and create_time <= #{endCreateTime, jdbcType=TIMESTAMP}



	        and producer like concat('%', #{producer,jdbcType=VARCHAR}, '%') 



	        and pro_no like concat('%', #{proNo,jdbcType=VARCHAR}, '%') 



	        and mat_name like concat('%', #{matName,jdbcType=VARCHAR}, '%') 



	        and mat_no like concat('%', #{matNo,jdbcType=VARCHAR}, '%') 



	        and mat_roducer like concat('%', #{matProducer,jdbcType=VARCHAR}, '%') 





  			order by ${custom_order_sql}


  			order by id desc
