mapper.select:
  	select 

      from task


	        and code like concat('%', #{code,jdbcType=VARCHAR}, '%') 



			and org_id = #{orgId,jdbcType=BIGINT}



			and i_id = #{iId,jdbcType=BIGINT}



			and state = #{state,jdbcType=INTEGER}



			and tg_labe = #{tgLabe,jdbcType=BIGINT}



			and inf_lab = #{infLab,jdbcType=BIGINT}



			and dt_lab = #{dtLab,jdbcType=BIGINT}



	    	and create_time >= #{startCreateTime, jdbcType=TIMESTAMP}



	    	and create_time <= #{endCreateTime, jdbcType=TIMESTAMP}





  			order by ${custom_order_sql}


  			order by id desc
