mapper.select:
  	select 

      from task t LEFT JOIN account a on t.a_id = a.id


	        and t.code like concat('%', #{code,jdbcType=VARCHAR}, '%') 



	        and a.nickname like concat('%', #{nickName,jdbcType=VARCHAR}, '%') 



			and t.org_id = #{orgId,jdbcType=BIGINT}



			and t.i_id = #{iId,jdbcType=BIGINT}



			and t.a_id = #{iId,jdbcType=BIGINT}



			and t.state = #{state,jdbcType=INTEGER}



			and t.atlas_lab = #{atlasLab,jdbcType=BIGINT}


			and t.pattern_lab = #{patternLab,jdbcType=BIGINT}



			and t.fail_num >= #{failNum,jdbcType=INTEGER}



			and t.atlas_result = #{atlasResult,jdbcType=INTEGER}



			and t.pattern_result = #{patternResult,jdbcType=INTEGER}



	    	and t.create_time >= #{startCreateTime, jdbcType=TIMESTAMP}



	    	and t.create_time <= #{endCreateTime, jdbcType=TIMESTAMP}





  			order by ${custom_order_sql}


  			order by t.id desc
