mapper.select:
select 
from task t LEFT JOIN account a on t.a_id = a.id


		and t.code like concat('%', #{code,jdbcType=VARCHAR}, '%')



		and t.code = #{qCode,jdbcType=VARCHAR}



		and a.nickname like concat('%', #{nickName,jdbcType=VARCHAR}, '%')



		and a.username like concat('%', #{userName,jdbcType=VARCHAR}, '%')



		and t.org_id = #{orgId,jdbcType=BIGINT}



		and t.i_id = #{iId,jdbcType=BIGINT}



		and t.a_id = #{applicatId,jdbcType=BIGINT}



		and t.t_id = #{tId,jdbcType=BIGINT}



		and t.state = #{state,jdbcType=INTEGER}



		and t.draft = #{draft,jdbcType=INTEGER}



		and t.draft != #{neDraft,jdbcType=INTEGER}




		and t.state != 7 and t.state != 9 and t.state != 11



		and t.type = #{type,jdbcType=INTEGER}



		and (t.state = 1 or t.state = 2)



		and t.parts_atl_id = #{partsAtlId,jdbcType=BIGINT}



		and t.mat_atl_id = #{matAtlId,jdbcType=BIGINT}



		and t.parts_pat_id = #{partsPatId,jdbcType=BIGINT}



		and t.mat_pat_id = #{matPatId,jdbcType=BIGINT}



		and t.parts_atl_times = #{partsAtlTimes,jdbcType=INTEGER}



		and t.mat_atl_times = #{matAtlTimes,jdbcType=INTEGER}



		and t.parts_pat_times = #{partsPatTimes,jdbcType=INTEGER}



		and t.mat_pat_times = #{matPatTimes,jdbcType=INTEGER}



		and t.fail_num >= #{failNum,jdbcType=INTEGER}



		and t.parts_atl_result = #{partsAtlResult,jdbcType=INTEGER}



		and t.mat_atl_result = #{matAtlResult,jdbcType=INTEGER}



		and t.parts_pat_result = #{partsPatResult,jdbcType=INTEGER}



		and t.mat_pat_result = #{matPatResult,jdbcType=INTEGER}



		and t.create_time >= #{startCreateTime, jdbcType=TIMESTAMP}



		and t.create_time <= #{endCreateTime, jdbcType=TIMESTAMP}



		and t.confirm_time >= #{startConfirmTime, jdbcType=TIMESTAMP}



		and t.confirm_time <= #{endConfirmTime, jdbcType=TIMESTAMP}



		and t.info_apply = #{infoApply,jdbcType=INTEGER}


		and t.result_apply = #{resultApply,jdbcType=INTEGER}




		and (t.parts_atl_id is null and t.mat_atl_id is null)




		and ((t.mat_atl_id is null or t.mat_pat_id is null or t.parts_atl_id IS NULL OR t.parts_pat_id IS NULL) or (( t.mat_atl_result = 0 AND t.mat_atl_id IS NOT NULL ) OR ( t.mat_pat_result = 0 AND t.mat_pat_id IS NOT NULL ) OR ( t.parts_pat_result = 0 AND t.parts_pat_id IS NOT NULL ) OR ( t.parts_atl_result = 0 AND t.parts_atl_id IS NOT NULL )))




		and ((t.state = 3 and (( t.parts_atl_result = 0 AND t.parts_atl_id IS NOT NULL ) OR ( t.mat_atl_result = 0 AND t.mat_atl_id IS NOT NULL ))) or (t.info_apply = 1 or (t.result_apply = 1 and t_id is not null)))




		and ((t.state = 3 and ((t.mat_atl_result = 0 and t.mat_atl_id IS NOT NULL) or (t.mat_pat_result = 0 and t.mat_pat_id IS NOT NULL ) or (t.parts_pat_result = 0 and t.parts_pat_id IS NOT NULL) or (t.parts_atl_result = 0 and t.parts_atl_id IS NOT NULL))) or (t.info_apply = 1 or (t.result_apply = 1 and t_id is not null)))




		and (t.state = 1 or (t.state = 8 and (t.info_apply = 1 or (t.result_apply = 1 and t_id is not null))))




		and ((t.parts_atl_id = #{atlasTask, jdbcType=BIGINT} and t.parts_atl_result = 1 ) or (t.mat_atl_id = #{atlasTask, jdbcType=BIGINT} and t.mat_atl_result = 1))



		and ( t.parts_atl_result = 1 or t.mat_atl_result = 1 )




		and ((t.parts_pat_id = #{patternTask, jdbcType=BIGINT} and t.parts_pat_result = 1 ) or (t.mat_pat_id = #{patternTask, jdbcType=BIGINT} and t.mat_pat_result = 1))



		and ( t.parts_pat_result = 1 or t.mat_pat_result = 1)




		and ( t.type = 2 or t.type = 3) and (parts_atl_id = #{compareTask, jdbcType=BIGINT} or mat_atl_id = #{compareTask, jdbcType=BIGINT})




	    and ((state =3 and (type = 1 or type = 4)) or (state = 5 and (type = 2 or type = 3))) and ((t.parts_pat_id = #{sendTask, jdbcType=BIGINT} and t.parts_pat_result = 2 ) or (t.mat_pat_id = #{sendTask, jdbcType=BIGINT} and t.mat_pat_result = 2) or (t.parts_atl_id = #{sendTask, jdbcType=BIGINT} and t.parts_atl_result = 2) or (t.mat_atl_id = #{sendTask, jdbcType=BIGINT} and t.mat_atl_result = 2))



		and ((state =3 and (type = 1 or type = 4)) or (state = 5 and (type = 2 or type = 3))) and (t.parts_pat_result = 2  or t.mat_pat_result = 2 or t.parts_atl_result = 2 or t.mat_atl_result = 2)




		and (t.type = 4 AND (( t.mat_atl_code IS NOT NULL AND t.mat_atl_times > 0 ) OR ( t.parts_atl_code IS NOT NULL AND t.parts_atl_times > 0 ) OR ( t.mat_pat_code IS NOT NULL AND t.mat_pat_times > 0 ) OR ( t.parts_pat_times > 0 AND t.parts_pat_code IS NOT NULL )) AND (( t.mat_atl_code IS NOT NULL AND t.mat_atl_times = 0 ) OR ( t.parts_atl_code IS NOT NULL AND t.parts_atl_times = 0 ) OR ( t.mat_pat_code IS NOT NULL AND t.mat_pat_times = 0 ) OR ( parts_pat_times = 0 AND t.parts_pat_code IS NOT NULL )))




		and ((t.type != 4 and (t.mat_atl_result = 3 or t.parts_atl_result = 3)) or ( t.type = 4 AND (( t.mat_atl_code IS NOT NULL AND t.mat_atl_times > 0 ) OR ( t.parts_atl_code IS NOT NULL AND t.parts_atl_times > 0 ) OR ( t.mat_pat_code IS NOT NULL AND t.mat_pat_times > 0 ) OR ( t.parts_pat_times > 0 AND t.parts_pat_code IS NOT NULL )) AND NOT (( t.mat_atl_code IS NOT NULL AND t.mat_atl_times = 0 ) OR ( t.parts_atl_code IS NOT NULL AND t.parts_atl_times = 0 ) OR ( t.mat_pat_code IS NOT NULL AND t.mat_pat_times = 0 ) OR ( parts_pat_times = 0 AND t.parts_pat_code IS NOT NULL ))))




		and (((t.type = 1 or t.type = 4) and (t.state = 4 or t.state = 5)) or ((t.type = 2 or t.type = 3) and (t.state = 7 or t.state = 8))) and (t.info_apply != 1 and t.result_apply != 1 and t.result_apply != 2)




		and (t.parts_pat_id = #{accomplishTask_lab, jdbcType=BIGINT} or t.mat_pat_id = #{accomplishTask_lab, jdbcType=BIGINT} or t.parts_atl_id = #{accomplishTask_lab, jdbcType=BIGINT} or t.mat_atl_id = #{accomplishTask_lab, jdbcType=BIGINT}) and (((t.type = 1 or t.type = 4) and (t.state = 4 or t.state = 5)) or ((t.type = 2 or t.type = 3) and (t.state = 7 or t.state = 8)))  and (t.info_apply != 1 and t.result_apply != 1 and t.result_apply != 2)




		and (a.id = #{queryMap.accountId, jdbcType=BIGINT} or parts_atl_id = #{queryMap.orgId, jdbcType=BIGINT} or mat_atl_id = #{queryMap.orgId, jdbcType=BIGINT} or parts_pat_id = #{queryMap.orgId, jdbcType=BIGINT} or mat_pat_id = #{queryMap.orgId, jdbcType=BIGINT}

			or t.id in  #{id}  

		)




		and t.id in  #{id}  




		and t.i_id in  #{id}  




		and t.a_id in  #{id}  




		and t.reason_id in  #{id}  



		and t.result = #{result,jdbcType=INTEGER}



		and t.is_receive = #{isReceive,jdbcType=INTEGER}



		and t.reason_id = #{reasonId,jdbcType=BIGINT}



		and t.expect_date >= #{startExpectDate, jdbcType=DATE}



		and t.expect_date <= #{endExpectDate, jdbcType=DATE}



		and t.stand_i_id = #{standIid,jdbcType=BIGINT}



		and t.approve_account_id = #{approveAccountId,jdbcType=BIGINT}



		and t.trainsmit_account_id = #{trainsmitAccountId,jdbcType=BIGINT}



		and t.examine_account_id = #{examineAccountId,jdbcType=BIGINT}




		and (t.type = 1 or type = 4)




		and (t.type = 2 or type = 3)




		and ((t.approve_account_id = #{currentAccountId,jdbcType=BIGINT} or t.trainsmit_account_id = #{currentAccountId,jdbcType=BIGINT} or t.examine_account_id = #{currentAccountId,jdbcType=BIGINT} or t.a_id = #{currentAccountId,jdbcType=BIGINT}) or (t.parts_pat_id = #{labId, jdbcType=BIGINT} or t.mat_pat_id = #{labId, jdbcType=BIGINT} or t.parts_atl_id = #{labId, jdbcType=BIGINT} or t.mat_atl_id = #{labId, jdbcType=BIGINT}))




		and (t.a_id = #{applyTask,jdbcType=BIGINT} or (t.parts_pat_id = #{labId, jdbcType=BIGINT} or t.mat_pat_id = #{labId, jdbcType=BIGINT} or t.parts_atl_id = #{labId, jdbcType=BIGINT} or t.mat_atl_id = #{labId, jdbcType=BIGINT}))





		and t.type in  #{id}  





		order by ${custom_order_sql}


		order by t.id desc

select ifnull(count(T.id),0) as totalNum from task t LEFT JOIN account a on t.a_id = a.id


		and t.state = #{state,jdbcType=INTEGER}



		and t.type = #{type,jdbcType=INTEGER}



		and t.approve_account_id = #{approveAccountId,jdbcType=BIGINT}




		and ((t.state = 3 and (( t.parts_atl_result = 0 AND t.parts_atl_id IS NOT NULL ) OR ( t.mat_atl_result = 0 AND t.mat_atl_id IS NOT NULL ))) or (t.info_apply = 1 or (t.result_apply = 1 and t_id is not null)))




		and ((t.state = 3 and ((t.mat_atl_result = 0 and t.mat_atl_id IS NOT NULL) or (t.mat_pat_result = 0 and t.mat_pat_id IS NOT NULL ) or (t.parts_pat_result = 0 and t.parts_pat_id IS NOT NULL) or (t.parts_atl_result = 0 and t.parts_atl_id IS NOT NULL))) or (t.info_apply = 1 or (t.result_apply = 1 and t_id is not null)))




		and (t.state = 1 or (t.state = 8 and (t.info_apply = 1 or (t.result_apply = 1 and t_id is not null))))



		and t.trainsmit_account_id = #{trainsmitAccountId,jdbcType=BIGINT}



		and t.examine_account_id = #{examineAccountId,jdbcType=BIGINT}




		and t.state != 7 and t.state != 9



		and (t.state = 1 or t.state = 2)



		and t.info_apply = #{infoApply,jdbcType=INTEGER}


		and t.result_apply = #{resultApply,jdbcType=INTEGER}




		and (t.parts_atl_id is null and t.mat_atl_id is null)




		and ((t.mat_atl_id is null or t.mat_pat_id is null or t.parts_atl_id IS NULL OR t.parts_pat_id IS NULL) or (( t.mat_atl_result = 0 AND t.mat_atl_id IS NOT NULL ) OR ( t.mat_pat_result = 0 AND t.mat_pat_id IS NOT NULL ) OR ( t.parts_pat_result = 0 AND t.parts_pat_id IS NOT NULL ) OR ( t.parts_atl_result = 0 AND t.parts_atl_id IS NOT NULL )))




		and ((t.parts_atl_id = #{atlasTask, jdbcType=BIGINT} and t.parts_atl_result = 1 ) or (t.mat_atl_id = #{atlasTask, jdbcType=BIGINT} and t.mat_atl_result = 1))




		and ((t.parts_pat_id = #{patternTask, jdbcType=BIGINT} and t.parts_pat_result = 1 ) or (t.mat_pat_id = #{patternTask, jdbcType=BIGINT} and t.mat_pat_result = 1))




		and ( t.type = 2 or t.type = 3) and (parts_atl_id = #{compareTask, jdbcType=BIGINT} or mat_atl_id = #{compareTask, jdbcType=BIGINT})




	    and ((state =3 and (type = 1 or type = 4)) or (state = 5 and (type = 2 or type = 3))) and ((t.parts_pat_id = #{sendTask, jdbcType=BIGINT} and t.parts_pat_result = 2 ) or (t.mat_pat_id = #{sendTask, jdbcType=BIGINT} and t.mat_pat_result = 2) or (t.parts_atl_id = #{sendTask, jdbcType=BIGINT} and t.parts_atl_result = 2) or (t.mat_atl_id = #{sendTask, jdbcType=BIGINT} and t.mat_atl_result = 2))




		and (t.type = 4 AND (( t.mat_atl_code IS NOT NULL AND t.mat_atl_times > 0 ) OR ( t.parts_atl_code IS NOT NULL AND t.parts_atl_times > 0 ) OR ( t.mat_pat_code IS NOT NULL AND t.mat_pat_times > 0 ) OR ( t.parts_pat_times > 0 AND t.parts_pat_code IS NOT NULL )) AND (( t.mat_atl_code IS NOT NULL AND t.mat_atl_times = 0 ) OR ( t.parts_atl_code IS NOT NULL AND t.parts_atl_times = 0 ) OR ( t.mat_pat_code IS NOT NULL AND t.mat_pat_times = 0 ) OR ( parts_pat_times = 0 AND t.parts_pat_code IS NOT NULL )))




		and ((t.type != 4 and (t.mat_atl_result = 3 or t.parts_atl_result = 3)) or ( t.type = 4 AND (( t.mat_atl_code IS NOT NULL AND t.mat_atl_times > 0 ) OR ( t.parts_atl_code IS NOT NULL AND t.parts_atl_times > 0 ) OR ( t.mat_pat_code IS NOT NULL AND t.mat_pat_times > 0 ) OR ( t.parts_pat_times > 0 AND t.parts_pat_code IS NOT NULL )) AND NOT (( t.mat_atl_code IS NOT NULL AND t.mat_atl_times = 0 ) OR ( t.parts_atl_code IS NOT NULL AND t.parts_atl_times = 0 ) OR ( t.mat_pat_code IS NOT NULL AND t.mat_pat_times = 0 ) OR ( parts_pat_times = 0 AND t.parts_pat_code IS NOT NULL ))))




		and t.a_id = #{applicatId,jdbcType=BIGINT}

select id, code, type, state, create_time, a_id, taskType from (
	select id, code, type, state, create_time, a_id, 0 taskType from task where code = null



		union all select id, code, type, state, create_time, a_id, 1 taskType from task where type = 1 and (state = 1 or state = 2) and a_id = #{applicatId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 2 taskType from task where type = 1 and (state = 1 or state = 2) and examine_account_id = #{applicatId,jdbcType=BIGINT} and draft != 1




		union all select id, code, type, state, create_time, a_id, 3 taskType from task where type = 1 and state = 3 and parts_atl_id is null and mat_atl_id is null and trainsmit_account_id = #{applicatId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 4 taskType from task where type = 1 and ((state = 3 and (( parts_atl_result = 0 AND parts_atl_id IS NOT NULL ) OR ( mat_atl_result = 0 AND mat_atl_id IS NOT NULL ))) or (info_apply = 1 or (result_apply = 1 and t_id is not null))) and approve_account_id = #{applicatId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 5 taskType from task where type = 2 and state = 2 and a_id = #{applicatId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 6 taskType from task where type = 2 and (state = 1 or (state = 8 and (info_apply = 1 or (result_apply = 1 and t_id is not null)))) and approve_account_id = #{applicatId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 7 taskType from task where type = 3 and state = 2 and a_id = #{applicatId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 8 taskType from task where type = 3 and (state = 1 or (state = 8 and (info_apply = 1 or (result_apply = 1 and t_id is not null)))) and approve_account_id = #{applicatId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 9 taskType from task where type = 4 and (state = 1 or state = 2) and a_id = #{applicatId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 10 taskType from task where type = 4 and (state = 1 or state = 2) and examine_account_id = #{applicatId,jdbcType=BIGINT} and draft != 1




		union all select id, code, type, state, create_time, a_id, 11 taskType from task where type = 4 and state = 3 and trainsmit_account_id = #{applicatId,jdbcType=BIGINT} and ((mat_atl_id is null or mat_pat_id is null or parts_atl_id IS NULL OR parts_pat_id IS NULL) or ((mat_atl_result = 0 AND mat_atl_id IS NOT NULL ) OR ( mat_pat_result = 0 AND mat_pat_id IS NOT NULL ) OR ( parts_pat_result = 0 AND parts_pat_id IS NOT NULL ) OR ( parts_atl_result = 0 AND parts_atl_id IS NOT NULL )))




		union all select id, code, type, state, create_time, a_id, 12 taskType from task where type = 4 and ((state = 3 and ((mat_atl_result = 0 and mat_atl_id IS NOT NULL) or (mat_pat_result = 0 and mat_pat_id IS NOT NULL ) or (parts_pat_result = 0 and parts_pat_id IS NOT NULL) or (parts_atl_result = 0 and parts_atl_id IS NOT NULL))) or (info_apply = 1 or (result_apply = 1 and t_id is not null))) and approve_account_id = #{approveAccountId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 13 taskType from task where state = 3 and ((parts_pat_id = #{patternTask, jdbcType=BIGINT} and parts_pat_result = 1 ) or (mat_pat_id = #{patternTask, jdbcType=BIGINT} and mat_pat_result = 1))




		union all select id, code, type, state, create_time, a_id, 14 taskType from task where state = 3 and ((parts_atl_id = #{atlasTask, jdbcType=BIGINT} and parts_atl_result = 1 ) or (mat_atl_id = #{atlasTask, jdbcType=BIGINT} and mat_atl_result = 1))




		union all select id, code, type, state, create_time, a_id, 15 taskType from task where state = 4 and ( type = 2 or type = 3) and (parts_atl_id = #{compareTask, jdbcType=BIGINT} or mat_atl_id = #{compareTask, jdbcType=BIGINT})




		union all select id, code, type, state, create_time, a_id, 16 taskType from task where ((state =3 and (type = 1 or type = 4)) or (state = 5 and (type = 2 or type = 3))) and ((parts_pat_id = #{sendTask, jdbcType=BIGINT} and parts_pat_result = 2 ) or (mat_pat_id = #{sendTask, jdbcType=BIGINT} and mat_pat_result = 2) or (parts_atl_id = #{sendTask, jdbcType=BIGINT} and parts_atl_result = 2) or (mat_atl_id = #{sendTask, jdbcType=BIGINT} and mat_atl_result = 2))




		union all select id, code, type, state, create_time, a_id, 17 taskType from task where type = 4 and a_id = #{applicatId,jdbcType=BIGINT} AND (( mat_atl_code IS NOT NULL AND mat_atl_times > 0 ) OR ( parts_atl_code IS NOT NULL AND parts_atl_times > 0 ) OR ( mat_pat_code IS NOT NULL AND mat_pat_times > 0 ) OR ( parts_pat_times > 0 AND parts_pat_code IS NOT NULL )) AND (( mat_atl_code IS NOT NULL AND mat_atl_times = 0 ) OR ( parts_atl_code IS NOT NULL AND parts_atl_times = 0 ) OR ( mat_pat_code IS NOT NULL AND mat_pat_times = 0 ) OR ( parts_pat_times = 0 AND parts_pat_code IS NOT NULL )) and a_id = #{applicatId,jdbcType=BIGINT}




		union all select id, code, type, state, create_time, a_id, 18 taskType from task where (type != 4 and a_id = #{applicatId,jdbcType=BIGINT} and (mat_atl_result = 3 or parts_atl_result = 3)) or ( type = 4 and a_id = #{applicatId,jdbcType=BIGINT} AND (( mat_atl_code IS NOT NULL AND mat_atl_times > 0 ) OR ( parts_atl_code IS NOT NULL AND parts_atl_times > 0 ) OR ( mat_pat_code IS NOT NULL AND mat_pat_times > 0 ) OR ( parts_pat_times > 0 AND parts_pat_code IS NOT NULL )) AND NOT (( mat_atl_code IS NOT NULL AND mat_atl_times = 0 ) OR ( parts_atl_code IS NOT NULL AND parts_atl_times = 0 ) OR ( mat_pat_code IS NOT NULL AND mat_pat_times = 0 ) OR ( parts_pat_times = 0 AND parts_pat_code IS NOT NULL )))


) t 



		order by ${custom_order_sql}


		order by t.id desc
